import './App.css';
import React, { Component } from 'react'
import './pages/NewItemForm'
import NewItemForm from './pages/NewItemForm';
import ShowItem from './ShowItem'
import {Switch, Route} from 'react-router-dom'

let baseUrl = 'http://localhost:3003'

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      items: []
    }
  }

  getItems = () => {
    // fetch to the backend
    fetch(baseUrl)
    .then(res => {
      if(res.status === 200) {
        return res.json()
      } else {
        return []
      }
    }).then(data => {
      console.log(data)
      this.setState({ items: data })
    })
  }

  addItems = (newItem) => {
    const copyItems = [...this.state.items]
    copyItems.push(newItem)
    this.setState({
      items:copyItems
    })
  }

  deleteItem = (id) => {
    fetch(baseUrl+'/'+id,{
      method: 'DELETE'
    }).then(res => {
      const findIndex = this.state.items.findIndex(item => item._id === id)
      const copyItems = [...this.state.items]
      copyItems.splice(findIndex, 1)
      this.setState({
        items: copyItems
      })
    })
  }

  componentDidMount() {
    this.getItems()
  }

  render() {
    return (
      <div className="App">

        <div>
        <header className="header">
            <div className="homepage-link">
                <button>
                    &#9776;
                </button>
                <a href="">Ecommerce Website</a>
            </div>
            <div className="header-links">
                <a href="/new">Add a New Item</a>
                <a href="/cart">Cart</a>
                <a href="/signin">Sign In</a> 
            </div>
        </header>
        <main className="main">
            <div className="product-page">
            <NewItemForm baseUrl={baseUrl} addItems={this.addItems}/>
                <ul className="product-list">
                { this.state.items.map((item, i) => {
                return (
                    <li>
                        <div className="product" key={item._id}>
                            <div className="product-name">
                                <a href="">{item.name}</a>
                            </div>
                            <div className="product-description">
                                {item.description}
                            </div>
                            <div className="product-price">
                                {item.price}
                            </div>
                            <button onClick={() => this.deleteItem(item._id)}>Delete</button>
                        </div>
                    </li>
                    )
                  })
                }
                </ul>
            </div>
        </main>
        <footer className="footer">
            Group 6, SEI-Bromeliad.
        </footer>
    </div>
      </div>
    );
  }

}

// {/* <Route exact path='/new' component={() => <NewItemForm baseUrl={baseUrl} addItems={this.addItems}/>}/> */}
// {/* <Route exact path= '/show' component={ShowItem} /> */}


export default App;
