import React from 'react';
import { Switch, Route } from 'react-router-dom';

import Homepage from '../pages/Editpage';
import NewItemForm from '../pages/NewItemForm';
import ShowItem from '../pages/ShowItem';
import Homescreen from '../pages/Homescreen'
import Cart from '../pages/Cart'

let baseUrl = 'http://localhost:3003'

const Main = () => { 
  return (
    <Switch> {/* The Switch decides which component to show based on the current URL.*/}
      {/* <Route exact path='/' component={Homepage}></Route> */}
      <Route exact path='/new' component={() => <NewItemForm baseUrl={baseUrl} addItems={Homepage.addItems}/>}></Route>
      <Route exact path='/show/:id' component={ShowItem}></Route>
      <Route exact path='/' component={Homescreen}></Route>
      <Route exact path='/cart' component={Cart}></Route>
    </Switch>
  );
}


export default Main;
