import React, {Component} from "react";
import { useState,useEffect } from "react";
import { useDispatch,useSelector } from "react-redux";

import { getProducts, getProductsDetails } from "../redux/actions/productActions";
import { addToCart } from "../redux/actions/cartActions";


const ShowItem = ({match,history}) => {

    const [qty, setQty] = useState(1)
    const dispatch = useDispatch()

    const productDetails = useSelector(state => state.getProductsDetails)
    const {loading,error,product} = productDetails

    useEffect(()=>{
        if(product && match.params.id !== product._id){
            dispatch(getProductsDetails(match.params.id))
        }
    }, [dispatch, product,match])

    return(
        <div></div>
    )
}
export default ShowItem
