import React, { Component} from 'react'
import {Link} from 'react-router-dom'
import {useEffect} from 're'


let baseUrl = 'http://localhost:3003'

class Homepage extends Component {
  constructor(props) {
    super(props)

    this.state = {
      items: []
    }
  }

  getItems = () => {
    fetch(baseUrl)
    .then(res => {
      if(res.status === 200) {
        return res.json()
      } else {
        return []
      }
    }).then(data => {
      console.log(data)
      this.setState({ items: data })
    })
  }

  addItems = (newItem) => {
    const copyItems = [...this.state.items]
    copyItems.push(newItem)
    this.setState({
      items:copyItems
    })
  }

  deleteItem = (id) => {
    fetch(baseUrl+'/'+id,{
      method: 'DELETE'
    }).then(res => {
      const findIndex = this.state.items.findIndex(item => item._id === id)
      const copyItems = [...this.state.items]
      copyItems.splice(findIndex, 1)
      this.setState({
        items: copyItems
      })
    })
  }

  componentDidMount() {
    this.getItems()
  }

  render() {
    return (
        <div>

        <main className="main">
            <div className="product-page">
                <ul className="product-list">
                { this.state.items.map((item, i) => {
                return (
                    <li key={item._id}>
                        <div className="product">
                            <div className="product-name">
                                <Link to={"/show/" + item._id}>
                                    {item.name}
                                </Link>
                            </div>
                            <div className="product-description">
                                {item.description}
                            </div>
                            <div className="product-price">
                                {item.price}
                            </div>
                            <button>Add to Cart</button>
                            <button onClick={() => this.deleteItem(item._id)}>Delete</button>
                        </div>
                    </li>
                    )
                  })
                }
                </ul>
            </div>
        </main>
        <footer className="footer">
            Group 6, SEI-Bromeliad.
        </footer>
    </div>

    );
  }
}

export default Homepage;
