import './App.css';
import React, { Component } from 'react'
import './NewItemForm'
import NewItemForm from './NewItemForm';

let baseUrl = 'http://localhost:3001'

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      items: []
    }
  }

  getItems = () => {
    // fetch to the backend
    fetch(baseUrl)
    .then(res => {
      if(res.status === 200) {
        return res.json()
      } else {
        return []
      }
    }).then(data => {
      console.log(data)
      this.setState({ items: data })
    })
  }

  addItems = (newItem) => {
    const copyItems = [...this.state.items]
    copyItems.push(newItem)
    this.setState({
      items:copyItems
    })
  }

  deleteItem = (id) => {
    fetch(baseUrl+'/'+id,{
      method: 'DELETE'
    }).then(res => {
      const findIndex = this.state.items.findIndex(item => item._id === id)
      const copyItems = [...this.state.items]
      copyItems.splice(findIndex, 1)
      this.setState({
        items: copyItems
      })
    })
  }

  componentDidMount() {
    this.getItems()
  }

  render() {
    return (

      <div className="App">

        <div>
          <header className="header">
            <div className="homepage-link">
              <button>
                &#9776;
              </button>
              <a href="">Ecommerce Website</a>
            </div>
            <div className="header-links">
              <a href="">Cart</a>
              <a href="">Sign In</a>
            </div>
          </header>
          <main className="main">
            <div className="product-page">
              <ul className="product-list">
              {this.state.items.map((item, i) => {
                return (
                  <li>
                    <div className="product" key={item._id}>
                      <div className="product-name">
                        {item.name}
                      </div>
                      {/* <div className="product-description">
                        Women Boots with Strap
                      </div>
                      <div className="product-price">
                        $65
                    </div> */}
                      <button onClick={() => this.deleteItem(item._id)}>Delete</button>
                    </div>
                  </li>
                );
              })}
              </ul>
            </div>
          </main>
          <footer className="footer">
            Group 6, SEI-Bromeliad.
          </footer>
        </div>
        </div>
    );
  }

}

export default App;
