import './App.css';
import React, { Component } from 'react'
import './NewItemForm'
import NewItemForm from './NewItemForm';

let baseUrl = 'http://localhost:3001'

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      items: []
    }
  }

  getItems = () => {
    // fetch to the backend
    fetch(baseUrl)
    .then(res => {
      if(res.status === 200) {
        return res.json()
      } else {
        return []
      }
    }).then(data => {
      console.log(data)
      this.setState({ items: data })
    })
  }

  addItems = (newItem) => {
    const copyItems = [...this.state.items]
    copyItems.push(newItem)
    this.setState({
      items:copyItems
    })
  }

  deleteItem = (id) => {
    fetch(baseUrl+'/'+id,{
      method: 'DELETE'
    }).then(res => {
      const findIndex = this.state.items.findIndex(item => item._id === id)
      const copyItems = [...this.state.items]
      copyItems.splice(findIndex, 1)
      this.setState({
        items: copyItems
      })
    })
  }

  componentDidMount() {
    this.getItems()
  }

  render() {
    return (

      <div className="App">
        <NewItemForm baseUrl={baseUrl} addItems={this.addItems}/>
        <table>
          <tbody>
            { this.state.items.map((item, i) => {
                return (
                  <tr key={item._id}>
                    <td>
                      {item.name}
                    </td>
                    <button onClick={() => this.deleteItem(item._id)}>Delete</button>
                  </tr>
                )
              })
            }
          </tbody>
        </table>
      </div>
    );
  }

}

export default App;
